package com.capstone.temfore.data.local.entityimport androidx.room.ColumnInfoimport androidx.room.Entityimport androidx.room.PrimaryKey@Entity(tableName = "favorite_food")data class FavoriteFood(    @PrimaryKey(autoGenerate = false)    @ColumnInfo(name = "foodId")    var foodId: String,    @ColumnInfo(name = "foodCategory")    var foodCategory: String,    @ColumnInfo(name = "foodTitle")    var foodTitle: String = "",    @ColumnInfo(name = "foodIngredients")    var foodIngredients: String? = null,    @ColumnInfo(name = "foodSteps")    var foodSteps: String? = null,    @ColumnInfo(name = "foodType")    var foodType: String? = null,    @ColumnInfo(name = "foodTemp")    var foodTemp: Int,    @ColumnInfo(name = "foodMedia")    var foodImage: String? = null,)