package com.capstone.temfore.data.local.roomimport androidx.lifecycle.LiveDataimport androidx.room.Daoimport androidx.room.Insertimport androidx.room.OnConflictStrategyimport androidx.room.Queryimport com.capstone.temfore.data.local.entity.FavoriteFoodimport com.capstone.temfore.data.remote.response.RecommendResponse@Daointerface FavoriteDao {    @Query("SELECT * FROM favorite_food")    fun getAllFavoriteFood(): LiveData<List<FavoriteFood>>    @Insert(onConflict = OnConflictStrategy.REPLACE)    fun addFavoriteEvent(events: FavoriteFood)    @Query("DELETE FROM favorite_food WHERE foodId = :id")    fun deleteFavoriteEventById(id: String)    @Query("SELECT EXISTS(SELECT 1 FROM favorite_food WHERE foodId = :id)")    fun isEventFavorite(id: String): LiveData<Boolean>}