package com.capstone.temfore.data.remote.responseimport com.google.gson.annotations.SerializedNamedata class RecommendResponse(	@field:SerializedName("listRecommend")//	val listRecommend: List<ListRecommendItem?>? = null,	val listRecommend: List<ListRecommendItem> = listOf(),	@field:SerializedName("detailFood")	val detailFood: ListRecommendItem? = null,	@field:SerializedName("hasilpencarian")	val hasilPencarian: List<ListRecommendItem> = listOf(),)data class ListRecommendItem(	@field:SerializedName("Steps")	val steps: String? = null,	@field:SerializedName("score")	val score: Double? = null,	@field:SerializedName("Type")	val type: String? = null,	@field:SerializedName("Category")	val category: String? = null,	@field:SerializedName("Ingredients")	val ingredients: String? = null,	@field:SerializedName("imageUrl")	val imageUrl: String? = null,	@field:SerializedName("Title")	val title: String? = null,	@field:SerializedName("Temp(cold)")	val tempCold: Int? = null,	@field:SerializedName("Id")	val id: Int? = null,	@field:SerializedName("URL")	val uRL: String? = null)