package com.capstone.temfore.ui.auth.loginimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport com.capstone.temfore.data.UserRepositoryimport com.capstone.temfore.data.pref.UserModelimport kotlinx.coroutines.launchclass LoginViewModel(private val repository: UserRepository) : ViewModel() {    fun saveSession(user: UserModel) {        viewModelScope.launch {            repository.saveSession(user)        }    }}