package com.capstone.temfore.ui.homeimport android.view.LayoutInflaterimport android.view.Viewimport android.view.ViewGroupimport android.widget.ImageViewimport android.widget.Toastimport androidx.recyclerview.widget.RecyclerViewimport com.capstone.temfore.Rclass CategoryAdapter(    private val imageList: List<Int>,    private val categoryList: List<String>, // Misalnya kategori dalam bentuk list string    private val onItemClick: (String) -> Unit // Callback untuk mengirim kategori) : RecyclerView.Adapter<CategoryAdapter.ImageViewHolder>() {    inner class ImageViewHolder(itemView: View) : RecyclerView.ViewHolder(itemView) {        val imageView: ImageView = itemView.findViewById(R.id.imageView)    }    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ImageViewHolder {        val view = LayoutInflater.from(parent.context).inflate(R.layout.item_category, parent, false)        return ImageViewHolder(view)    }    override fun onBindViewHolder(holder: ImageViewHolder, position: Int) {        holder.imageView.setImageResource(imageList[position])        holder.itemView.setOnClickListener {            // Panggil callback dengan kategori yang sesuai            val selectedCategory = categoryList[position]            onItemClick(selectedCategory)            // Tampilkan Toast dengan kategori yang dipilih            Toast.makeText(holder.itemView.context, "Kategori yang dipilih: $selectedCategory", Toast.LENGTH_SHORT).show()        }    }    override fun getItemCount(): Int = imageList.size}