package com.capstone.temfore.ui.homeimport android.content.Intentimport android.util.Logimport android.view.LayoutInflaterimport android.view.ViewGroupimport android.widget.Toastimport androidx.recyclerview.widget.DiffUtilimport androidx.recyclerview.widget.ListAdapterimport androidx.recyclerview.widget.RecyclerViewimport com.bumptech.glide.Glideimport com.capstone.temfore.Rimport com.capstone.temfore.data.remote.response.ListRecommendItemimport com.capstone.temfore.databinding.ItemRecommendationBindingimport com.capstone.temfore.ui.detail.DetailActivityimport com.capstone.temfore.utils.TextFormatclass RecommendationAdapter :    ListAdapter<ListRecommendItem, RecommendationAdapter.MyViewHolder>(DIFF_CALLBACK) {    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): MyViewHolder {        val binding =            ItemRecommendationBinding.inflate(LayoutInflater.from(parent.context), parent, false)        return MyViewHolder(binding)    }    override fun onBindViewHolder(holder: MyViewHolder, position: Int) {        val review = getItem(position)        holder.bind(review)    }    class MyViewHolder(private val binding: ItemRecommendationBinding) :        RecyclerView.ViewHolder(binding.root) {        fun bind(food: ListRecommendItem) {            binding.foodName.text = food.title            binding.ingredients.text = TextFormat.formatTemperatureMessage(food.tempCold)            binding.instructions.text = TextFormat.formatType(food.type)            binding.tvFoodScore.text = food.score?.let { TextFormat.formatScoreMessage(it) }            val imgCategory = TextFormat.getCategoryImage(food.category.toString())            Glide.with(binding.foodCategory.context)                .load(imgCategory)                .into(binding.foodCategory)            Glide.with(binding.foodImage.context)                .load(food.imageUrl)                .into(binding.foodImage)            binding.root.setOnClickListener {                val context = binding.root.context                val intent = Intent(context, DetailActivity::class.java)                intent.putExtra("FOOD_ID", food.id.toString())                context.startActivity(intent)                Log.d(                    "RecommendationAdapter",                    "Item clicked: ${food.title} - ID: ${food.id} - Type: ${food.id?.javaClass?.simpleName}"                )                Toast.makeText(context, "Pilih : ${food.title}", Toast.LENGTH_SHORT).show()            }        }    }    companion object {        val DIFF_CALLBACK = object : DiffUtil.ItemCallback<ListRecommendItem>() {            override fun areItemsTheSame(                oldItem: ListRecommendItem,                newItem: ListRecommendItem,            ): Boolean {                return oldItem == newItem            }            override fun areContentsTheSame(                oldItem: ListRecommendItem,                newItem: ListRecommendItem,            ): Boolean {                return oldItem == newItem            }        }    }}