package com.capstone.temfore.ui.homeimport androidx.lifecycle.ViewModelimport androidx.lifecycle.ViewModelProviderimport com.capstone.temfore.data.WeatherRepository@Suppress("UNCHECKED_CAST")// WeatherViewModelFactoryclass WeatherViewModelFactory(    private val weatherRepository: WeatherRepository) : ViewModelProvider.Factory {    override fun <T : ViewModel> create(modelClass: Class<T>): T {        return when {            modelClass.isAssignableFrom(WeatherViewModel::class.java) -> {                WeatherViewModel(weatherRepository) as T            }            else -> throw IllegalArgumentException("Unknown ViewModel class: ${modelClass.name}")        }    }}