package com.capstone.temfore.ui.homeimport android.util.Logimport androidx.lifecycle.LiveDataimport androidx.lifecycle.MutableLiveDataimport androidx.lifecycle.ViewModelimport androidx.lifecycle.viewModelScopeimport com.capstone.temfore.data.WeatherRepositoryimport com.capstone.temfore.data.remote.response.WeatherResponseimport kotlinx.coroutines.launchclass WeatherViewModel(    private val weatherRepository: WeatherRepository) : ViewModel() {    private val _weatherData = MutableLiveData<WeatherResponse>()    val weatherData: LiveData<WeatherResponse> get() = _weatherData    fun fetchWeatherByCoordinates(latitude: Double, longitude: Double) {        viewModelScope.launch {            try {                // Ambil data cuaca berdasarkan koordinat                val response = weatherRepository.getWeatherByCoordinates(latitude, longitude)                _weatherData.postValue(response)            } catch (e: Exception) {                // Tangani error jika terjadi                Log.e(TAG, "Error fetching weather: ${e.message}")            }        }    }    companion object {        const val TAG = "WeatherViewModel"    }}