package com.capstone.temfore.ui.onboardingimport androidx.appcompat.app.AppCompatActivityimport androidx.fragment.app.Fragmentimport androidx.viewpager2.adapter.FragmentStateAdapterclass OnboardingAdapter(    fragmentActivity: AppCompatActivity,    private val fragmentList: List<Fragment>) : FragmentStateAdapter(fragmentActivity) {    override fun getItemCount(): Int = fragmentList.size    override fun createFragment(position: Int): Fragment {        return fragmentList[position]    }}