package com.capstone.temfore.utilsimport java.text.SimpleDateFormatimport java.util.Calendarimport java.util.Localeclass TimeUtils {    private val timeHoursFormat = SimpleDateFormat("HH", Locale.getDefault())    private val timeFormat = SimpleDateFormat("HH:mm:ss", Locale.getDefault())    private val dateFormat = SimpleDateFormat("dd MMMM yyyy", Locale.getDefault())    fun getCurrentTime(): String = timeFormat.format(Calendar.getInstance().time)    fun getCurrentTimeHours(): String = timeHoursFormat.format(Calendar.getInstance().time)    fun getCurrentDate(): String = dateFormat.format(Calendar.getInstance().time)    fun getTimeBasedMessage(): String {        return when (Calendar.getInstance().get(Calendar.HOUR_OF_DAY)) {            in 6..9 -> "Sarapan!ðŸ¥ª"            in 11..13 -> "Makan Siang!ðŸ§º"            in 18..20 -> "Makan Malam!ðŸ´"            else -> "Mencoba Masakan!â˜ºï¸"        }    }    fun getHelloBasedMessage(): String {        return when (Calendar.getInstance().get(Calendar.HOUR_OF_DAY)) {            in 6..9 -> "Selamat Pagi,"            in 11..13 -> "Selamat Siang,"            in 18..20 -> "Selamat Malam,"            else -> "Selamat Datang,"        }    }}